css:
* {
    font-family: 'Cairo', sans-serif;
}

    .header1-table {
        text-align: center;
        font-size: 20px;
        
    }
    
    .header2-table {
        text-align: center;
        font-size: 20px;
        border: 1px solid black;
        border-collapse: collapse;
        
        
    }
    body {
      font-family: Arial, sans-serif;
      margin: 20px;
      color: black;
    }
    
    table {
      width: 100%;
      border-collapse: collapse;
      margin-bottom: 20px;
      font-weight: bold;
      text-align: center;
      color: black;
    }
    

    tr, th {
        color: black;
    }
    
    .center {
      text-align: center;
      color: black;
    }
    
    .right {
      text-align: center;
      color: black;
    }
    
    .bold {
      font-weight: bold;
      color: black;
    }
    
    /* Adjustments for the second table */
    .item-table {
      font-size: 12px;
      font-weight: bold;
      color: black;
      width: 100℅;
    }
    
    .header-table td, .item-table th, .item-table td {
      padding: 3px;
      font-weight: bold;
      color: black;
      border: 1px solid black;
      padding: 5px;
      font-weight: bold;
      text-align: center;
      color: black;
    }
    .details {
        width: 100%;
        height:50px;
        line-height: 70px;
        direction: ltr;
    }
    .qr-code {
        width: 50%;
        height: 50%;
        margin-top: 30px;
        max-width: 50%;
    }
    img {
        max-width: 50%;
    }
    .footer {
        display: block;
        width: 100%;
        bottom: 0;
        border: 1px solid black;
        font-weight: bold;
        text-align: center;
        font-size: 12px;
    }
    
    

tr:nth-child(even) {background-color: #f2f2f2;}
